package controller;import data.ProductIO;import java.io.IOException;import javax.servlet.ServletContext;import javax.servlet.ServletException;import javax.servlet.ServletRequest;import javax.servlet.ServletResponse;import javax.servlet.annotation.WebServlet;import javax.servlet.http.HttpServlet;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import javax.servlet.http.HttpSession;import model.Cart;import model.LineItem;import model.Product;@WebServlet({"/api/chap7cart/*"})public class chap7cart extends HttpServlet {  private static final long serialVersionUID = 1L;    protected void doPost(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {    String url = "/pages/c7e3/index.jsp";    ServletContext sc = getServletContext();    String action = request.getParameter("action");    if (action == null)      action = "cart";     if (action.equals("shop")) {      url = "/pages/c7e3/index.jsp";    } else if (action.equals("cart")) {      int quantity;      String productCode = request.getParameter("productCode");      String quantityString = request.getParameter("quantity");      HttpSession session = request.getSession();      Cart cart = (Cart)session.getAttribute("cart");      if (cart == null)        cart = new Cart();       try {        quantity = Integer.parseInt(quantityString);        if (quantity < 0)          quantity = 1;       } catch (NumberFormatException nfe) {        quantity = 1;      }       String path = sc.getRealPath("/WEB-INF/products.txt");      Product product = ProductIO.getProduct(productCode, path);      LineItem lineItem = new LineItem();      lineItem.setProduct(product);      lineItem.setQuantity(quantity);      if (quantity > 0) {        cart.addItem(lineItem);      } else if (quantity == 0) {        cart.removeItem(lineItem);      }       session.setAttribute("cart", cart);      url = "/pages/c7e3/cart.jsp";    } else if (action.equals("checkout")) {      url = "/pages/c7e3/checkout.jsp";    }     sc.getRequestDispatcher(url)      .forward((ServletRequest)request, (ServletResponse)response);  }    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {    doPost(request, response);  }}