package model;import java.io.Serializable;import java.text.NumberFormat;public class LineItem implements Serializable {  private static final long serialVersionUID = 1L;    private Product product;    private int quantity;    public void setProduct(Product p) {    this.product = p;  }    public Product getProduct() {    return this.product;  }    public void setQuantity(int quantity) {    this.quantity = quantity;  }    public int getQuantity() {    return this.quantity;  }    public double getTotal() {    double total = this.product.getPrice() * this.quantity;    return total;  }    public String getTotalCurrencyFormat() {    NumberFormat currency = NumberFormat.getCurrencyInstance();    return currency.format(getTotal());  }}