package model;import java.io.Serializable;import java.text.NumberFormat;public class Product implements Serializable {  private static final long serialVersionUID = 1L;    private String code = "";    private String description = "";    private double price = 0.0D;    public void setCode(String code) {    this.code = code;  }    public String getCode() {    return this.code;  }    public void setDescription(String description) {    this.description = description;  }    public String getDescription() {    return this.description;  }    public void setPrice(double price) {    this.price = price;  }    public double getPrice() {    return this.price;  }    public String getPriceCurrencyFormat() {    NumberFormat currency = NumberFormat.getCurrencyInstance();    return currency.format(this.price);  }}